<?phpinclude('settings.php');$con = dbConnect();if (!$con)  {  die('Could not connect: ' . mysql_error());  }if (mysql_query("CREATE DATABASE athome",$con))  {	dbSelect($con);  }else  {	dbSelect($con);	$sql = "CREATE TABLE schedule	(	id int(20) NOT NULL AUTO_INCREMENT, 	PRIMARY KEY(id),	name varchar(50),	active int(1)	)";	mysql_query($sql,$con);		$sql = "CREATE TABLE sprinklers	(	ID int(20) NOT NULL AUTO_INCREMENT, 	PRIMARY KEY(ID),	SchedID int(20),	Zone int(5),	StartTime varchar(4),	Length int(4),	Mon int(1),	Tue int(1),	Wed int(1),	Thu int(1),	Fri int(1),	Sat int(1),	Sun int(1)	)";	mysql_query($sql,$con);		$sql = "CREATE TABLE users	(	id int(10) NOT NULL AUTO_INCREMENT, 	PRIMARY KEY(id),	google varchar(500),	token varchar(255),	token_expire int(20)	)";	mysql_query($sql,$con);		mysql_query("INSERT INTO schedule (name, id, active) VALUES ('Default', '0', '1')");	mysql_query("INSERT INTO users (google) VALUES ('https://www.google.com/accounts/o8/id?id=AItOawnCxv8P2uk4FzvezjUrBAkDKpUw6bRM8A4')");	mysql_query("INSERT INTO sprinklers (SchedID, Zone, StartTime, Length, Mon, Tue, Wed, Thu, Fri, Sat, Sun) VALUES ('0', 0, 0, 1440, 1, 1, 1, 1, 1, 1, 1)");	mysql_query("UPDATE schedule SET id=0 WHERE name='Default'");	echo 'Setup Complete!';  }?>